import React, { useEffect, useState } from "react";
import { RadixDappToolkit } from '@radixdlt/radix-dapp-toolkit';

const Profile = () => {
  const [account, setAccount] = useState(null);
  const [rdt, setRdt] = useState(null);

  const loadAccount = async () => {
    try {
      const newAccount = await rdt.keyManager.createAccount();
      if (newAccount) {
        setAccount(newAccount);
      }
    } catch (error) {
      console.error("Error loading account:", error);
    }
  };

  useEffect(() => {
    const initializeToolkit = async () => {
      try {
        const newRdt = new RadixDappToolkit({
          nodeConnection: {
            websocketURL: 'ws://localhost:8080',
          },
          applicationId: 'Your-App-ID', // Replace 'Your-App-ID' with your actual application ID
          applicationName: 'Your App Name',
          applicationVersion: '1.0.0',
        });
        setRdt(newRdt);
      } catch (error) {
        console.error('Error initializing Radix Dapp Toolkit:', error);
      }
    };

    initializeToolkit();

    return () => {
      // Clean up if necessary
    };
  }, []);

  const handleConnectWallet = async () => {
    try {
      await rdt.account.connect();
      loadAccount();
    } catch (error) {
      console.error("Error connecting to Radix Wallet Connector:", error);
    }
  };

  return (
    <div className="container">
      <div className="aboutUs">
        <div className="welcome">
          <h2>Welcome Back, Alex!</h2>
          <p>Wow! You spent 725 minutes with your favorite artist this year!</p>
        </div>
        <div className="button-container">
          {!account && <button className="wallet-button" onClick={handleConnectWallet}>Connect Wallet</button>}
          {account && (
            <div>
              <p>Account Address: {account.getAddress()}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
