import React, { useState, useEffect } from 'react';
import { RadixDappToolkit, DataRequestBuilder, RadixNetwork } from '@radixdlt/radix-dapp-toolkit';

const Profile = () => {
  const [rdt, setRdt] = useState(null);
  const [loading, setLoading] = useState(true);
  const [accountAddress, setAccountAddress] = useState('');
  const [componentAddress, setComponentAddress] = useState('');
  const [adminBadge, setAdminBadge] = useState('');
  const [ownerBadge, setOwnerBadge] = useState('');
  const [gumResourceAddress, setGumResourceAddress] = useState('');

  useEffect(() => {
    const connectWallet = async () => {
      try {
        const dAppId = 'account_tdx_2_12xxk4dqhg9dz53p745qhpr5tr2k2al4mpx3296tr8k78kna6rkcgsz';
        const newRdt = RadixDappToolkit({
          dAppDefinitionAddress: dAppId,
          networkId: RadixNetwork.Stokenet,
          applicationName: 'Infinx',
          applicationVersion: '1.0.0',
        });
        setRdt(newRdt);
        setLoading(false);
        console.log('Connected to wallet successfully');

        // Fetch user's account address
        newRdt.walletApi.setRequestData(DataRequestBuilder.accounts().atLeast(1));
        newRdt.walletApi.walletData$.subscribe((walletData) => {
          console.log("Subscription wallet data: ", walletData);
          if (walletData && walletData.accounts && walletData.accounts.length > 0) {
            setAccountAddress(walletData.accounts[0].address);
          }
        });
      } catch (error) {
        console.error('Error connecting to Radix Wallet Connector:', error);
      }
    };

    connectWallet();
  }, []);

  const instantiateComponent = async () => {
    try {
      // Assuming you have a function called `instantiateComponent` in your RadixDappToolkit
      const componentInstance = await rdt.instantiateComponent();

      // Assuming the component instance object has properties like `address`, `adminBadge`, etc.
      setComponentAddress(componentInstance.address);
      setAdminBadge(componentInstance.adminBadge);
      setOwnerBadge(componentInstance.ownerBadge);
      setGumResourceAddress(componentInstance.gumResourceAddress);
    } catch (error) {
      console.error('Error instantiating component:', error);
    }
  };


  return (
    <div>
      <button onClick={instantiateComponent}>Instantiate Component</button>

      {/* Display account address if available */}
      {accountAddress && <div>Account Address: {accountAddress}</div>}
      {accountAddress && (
        <div>
          {/* Display other details only when account address is available */}
          <div>Component Address: {componentAddress}</div>
          <div>Admin Badge: {adminBadge}</div>
          <div>Owner Badge: {ownerBadge}</div>
          <div>Gumball Resource Address: {gumResourceAddress}</div>
        </div>
      )}
    </div>
  );
};

export default Profile;
