import React, { useState, useEffect } from 'react';
import { RadixDappToolkit, DataRequestBuilder, RadixNetwork } from '@radixdlt/radix-dapp-toolkit';

const Profile = () => {
  const [rdt, setRdt] = useState(null);
  const [loading, setLoading] = useState(true);
  const [accountAddress, setAccountAddress] = useState('');
  const [connected, setConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState('');
  const [smartContract, setSmartContract] = useState(null);
  const [walletBalance, setWalletBalance] = useState(0);

  const connectWallet = async () => {
    try {
      const dAppId = 'account_tdx_2_12xxk4dqhg9dz53p745qhpr5tr2k2al4mpx3296tr8k78kna6rkcgsz';
      const newRdt = RadixDappToolkit({
        dAppDefinitionAddress: dAppId,
        networkId: RadixNetwork.Stokenet,
        applicationName: 'Infinx',
        applicationVersion: '1.0.0',
        modules: {
          atomService: true,
        },
      });
      setRdt(newRdt);
      setLoading(true);
      console.log('Connected to wallet successfully');
      setConnected(true);

      newRdt.walletApi.setRequestData(DataRequestBuilder.accounts().atLeast(1));
      newRdt.walletApi.walletData$.subscribe((walletData) => {
        console.log("Subscription wallet data: ", walletData);
        if (walletData && walletData.accounts && walletData.accounts.length > 0) {
          setAccountAddress(walletData.accounts[0].address);
          setWalletAddress(walletData.accounts[0].address);
          setWalletBalance(walletData.accounts[0].balance); // Assuming wallet data includes balance
          setLoading(false);
        }
      });

      const atomService = newRdt.atomService;
      if (atomService) {
        const contract = await atomService.getAtomById('your_transaction_id');
        setSmartContract(contract);
      } else {
        console.warn('Atom service is not available');
        setSmartContract(null); // Reset smart contract state
      }
    } catch (error) {
      console.error('Error connecting to Radix Wallet Connector:', error);
    }
  };

  useEffect(() => {
    connectWallet();
  }, []);

  const instantiateComponent = async () => {
    try {
      // Your instantiation logic here
    } catch (error) {
      console.error('Error instantiating component:', error);
    }
  };

  const sendTransaction = async () => {
    try {
      const result = await rdt.walletApi.sendTransaction({
        /* transaction data */
      });
      console.log('Transaction result:', result);
    } catch (error) {
      console.error('Error sending transaction:', error);
    }
  };

  return (
    <div>
      {!connected && !loading && <button onClick={connectWallet}>Connect Wallet</button>}
      {!connected && loading && <div>Loading...</div>}
      {connected && <div>You are connected. Wallet Address: {walletAddress}</div>}
      {connected && <div>Wallet Balance: {walletBalance}</div>}

      <button onClick={instantiateComponent}>Instantiate Component</button>
      {connected && (
        <button onClick={sendTransaction}>Send Transaction</button>
      )}

      {accountAddress && (
        <div>
          <div>Account Address: {accountAddress}</div>
        </div>
      )}

      {smartContract && <div>Smart Contract: {JSON.stringify(smartContract)}</div>}
    </div>
  );
};

export default Profile;
