import React, { useEffect, useState } from "react";
import { RadixDappToolkit, RadixNetwork, DataRequestBuilder } from '@radixdlt/radix-dapp-toolkit';

const Profile = () => {
  const [account, setAccount] = useState(null);
  const [rdt, setRdt] = useState(null);

  const loadAccount = async () => {
    try {
      const newAccount = await rdt.keyManager.createAccount();
      if (newAccount) {
        setAccount(newAccount);
      }
    } catch (error) {
      console.error("Error loading account:", error);
    }
  };

  useEffect(() => {
    const initializeToolkit = async () => {
      try {
        const newRdt = new RadixDappToolkit({
          dAppDefinitionAddress: 'account_tdx_2_12xxk4dqhg9dz53p745qhpr5tr2k2al4mpx3296tr8k78kna6rkcgsz', // Use your dApp definition address
          networkId: RadixNetwork.Stokenet, // Replace with the appropriate network ID
          applicationName: 'Infix',
          applicationVersion: '1.0.0',
        });
        setRdt(newRdt);
      } catch (error) {
        console.error('Error initializing Radix Dapp Toolkit:', error);
      }
    };

    initializeToolkit();

    return () => {
      // Clean up if necessary
    };
  }, []);

  useEffect(() => {
    if (rdt) {
      // Fetch the user's account address
      rdt.walletApi.setRequestData(DataRequestBuilder.accounts().atLeast(1));
      rdt.walletApi.walletData$.subscribe((walletData) => {
        console.log("subscription wallet data: ", walletData);
        if (walletData.accounts.length > 0) {
          setAccount(walletData.accounts[0].address);
        }
      });
    }
  }, [rdt]);

  const handleConnectWallet = async () => {
    try {
      await rdt.account.connect();
      loadAccount();
    } catch (error) {
      console.error("Error connecting to Radix Wallet Connector:", error);
    }
  };

  return (
    <div className="container">
      <div className="aboutUs">
        <div className="welcome">
          <h2>Welcome Back, Alex!</h2>
          <p>Wow! You spent 725 minutes with your favorite artist this year!</p>
        </div>
        <div className="button-container">
          {!account && <button className="wallet-button" onClick={handleConnectWallet}>Connect Wallet</button>}
          {account && (
            <div>
              <p>Account Address: {account}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
