import "./profile.css";
import avata from "./y.jpg";
import graph from "./y.jpg";
import React, { useEffect, useState } from "react";
import { RadixDappToolkit, Account as RadixAccount } from '@radixdlt/radix-dapp-toolkit';

const Profile = () => {
  const [account, setAccount] = useState(null);

  const loadAccount = async () => {
    try {
      const account = await RadixAccount.create();
      if (account) {
        setAccount(account);
      }
    } catch (error) {
      console.error("Error loading account:", error);
    }
  };

  useEffect(() => {
    const initializeToolkit = async () => {
      try {
        // Initialize the Radix Dapp Toolkit
        const rdt = new RadixDappToolkit({
          nodeConnection: {
            websocketURL: 'ws://localhost:8080',
          },
          applicationName: 'Your App Name',
          applicationVersion: '1.0.0',
        });

        // Do something with the toolkit if needed
      } catch (error) {
        console.error('Error initializing Radix Dapp Toolkit:', error);
      }
    };

    initializeToolkit();

    loadAccount();

    return () => {
      // Clean up if necessary
    };
  }, []);

  const handleConnectWallet = async () => {
    try {
      // Initialize the Radix Dapp Toolkit
      const rdt = new RadixDappToolkit({
        nodeConnection: {
          websocketURL: 'ws://localhost:8080',
        },
        applicationName: 'Your App Name',
        applicationVersion: '1.0.0',
      });

      // Connect to Radix Wallet Connector
      await rdt.account.connect();
      loadAccount();
    } catch (error) {
      console.error("Error connecting to Radix Wallet Connector:", error);
    }
  };

  return (
    <div className="container">
      <div className="aboutUs">
        <div className="welcome">
          <h2>Welcome Back, Alex!</h2>
          <p>Wow! You spent 725 minutes with your favorite artist this year!</p>
        </div>
        <div className="grid-container">
          <div className="section" style={{ width: "100%" }}>
            <div className="profile">
              <img className="avatar2" src={avata} alt="Avatar" />
            </div>
          </div>
          <div className="section" style={{ width: "100%" }}>
            <div className="calculator">
              <img className="avatar3" src={graph} alt="Avatar" />
            </div>
            <div className="landing">
            </div>
          </div>
        </div>
        <div className="button-container">
          {!account && <button className="wallet-button" onClick={handleConnectWallet}>Connect Wallet</button>}
          {account && (
            <div>
              <p>Account Address: {account.getAddress()}</p>
            </div>
          )}
        </div>
        <iframe
          title="Portfolio Accounts"
          src="https://launch.shardspace.app/portfolio/accounts"
          width="100%"
          height="600px" // Adjust height as needed
          style={{ border: "none", marginTop: "20px" }}
        />
      </div>
    </div>
  );
};

export default Profile;
