import React, { useState, useEffect } from 'react';
import {
  DataRequestBuilder,
  RadixDappToolkit,
  RadixNetwork,
} from '@radixdlt/radix-dapp-toolkit';

const Profile = () => {
  const [accountAddress, setAccountAddress] = useState('');
  const [walletConnected, setWalletConnected] = useState(false);

  useEffect(() => {
    // Define your dApp definition address
    const dAppDefinitionAddress = 'account_tdx_2_12xxk4dqhg9dz53p745qhpr5tr2k2al4mpx3296tr8k78kna6rkcgsz'; // Update with your dApp definition address

    // Create a dapp configuration object for the Radix Dapp Toolkit
    const dappConfig = {
      networkId: RadixNetwork.Stokenet,
      applicationVersion: '1.0.0',
      applicationName: 'Infinix',
      applicationDappDefinitionAddress: dAppDefinitionAddress,
      dAppDefinitionAddress, // This field will be updated and removed soon
    };

    // Instantiate DappToolkit to connect to the Radix wallet and network
    const rdt = RadixDappToolkit(dappConfig);

    // Connect a user account when wallet is connected
    rdt.walletApi.setRequestData(DataRequestBuilder.accounts().exactly(1));

    // Subscribe to updates to the user's shared wallet data
    const subscription = rdt.walletApi.walletData$.subscribe((walletData) => {
      if (walletData && walletData.accounts && walletData.accounts.length > 0) {
        setAccountAddress(walletData.accounts[0].address);
        setWalletConnected(true);
      }
    });

    // Cleanup subscription on unmount
    return () => subscription.unsubscribe();
  }, []); // Only run this effect once on component mount

  return (
    <div>
      {walletConnected ? (
        <div>
          <p>Wallet Connected Successfully</p>
          <p>Account Address: {accountAddress}</p>
          {/* Add additional wallet details here */}
        </div>
      ) : (
        <p>Connecting to Wallet...</p>
      )}
    </div>
  );
};

export default Profile;
