import "./profile.css";
import avata from "./y.jpg";
import graph from "./y.jpg";
import React, { useEffect, useState } from "react";
import { RadixDappToolkit, RadixAccount } from '@radixdlt/radix-dapp-toolkit';

const Profile = () => {
  const [account, setAccount] = useState(null);

  useEffect(() => {
    // Initialize Radix Dapp Toolkit and load account
    const initializeToolkit = async () => {
      try {
        // Initialize Radix Dapp Toolkit
        const rdt = new RadixDappToolkit({
          nodeConnection: {
            websocketURL: 'ws://localhost:8080',
          },
        });

        // Load account after toolkit is initialized
        loadAccount();
      } catch (error) {
        console.error('Error initializing Radix Dapp Toolkit:', error);
      }
    };

    initializeToolkit();

    return () => {
      // Clean up if necessary
    };
  }, []);

  const loadAccount = async () => {
    try {
      // Create RadixAccount instance
      const radixAccount = await RadixAccount.create();
      setAccount(radixAccount);
    } catch (error) {
      console.error("Error loading account:", error);
    }
  };

  const handleConnectWallet = async () => {
    try {
      // Check if account is loaded
      if (account) {
        // Connect to Radix Wallet Connector
        await account.connect();
        // Reload the account after connecting
        loadAccount();
      } else {
        console.error("Account not loaded. Please wait for account to be loaded before connecting.");
      }
    } catch (error) {
      console.error("Error connecting to Radix Wallet Connector:", error);
    }
  };

  return (
    <div className="container">
      <div className="aboutUs">
        <div className="welcome">
          <h2>Welcome Back, Alex!</h2>
          <p>Wow! You spent 725 minutes with your favourite artist this year!</p>
        </div>
        <div className="grid-container">
          <div className="section" style={{ width: "100%" }}>
            <div className="profile">
              <img className="avatar2" src={avata} alt="Avatar" />
            </div>
          </div>
          <div className="section" style={{ width: "100%" }}>
            <div className="calculator">
              <img className="avatar3" src={graph} alt="Avatar" />
            </div>
            <div className="landing">
            </div>
          </div>
        </div>
        <div className="button-container">
          {/* Display connect wallet button if account is not loaded */}
          {!account && <button className="wallet-button" onClick={handleConnectWallet}>Connect Wallet</button>}
          {/* Display account information if loaded */}
          {account && (
            <div>
              <p>Account Address: {account.getAddress()}</p>
            </div>
          )}
        </div>
        {/* Add iframe to display external page */}
        <iframe
          title="Portfolio Accounts"
          src="https://launch.shardspace.app/portfolio/accounts"
          width="100%"
          height="600px" // Adjust height as needed
          style={{ border: "none", marginTop: "20px" }}
        />
      </div>
    </div>
  );
};

export default Profile;
